import { InputProps } from 'component/column/mf_input';
import { PicklistProps } from 'component/column/mf_picklist';
import { TextareaProps } from 'component/column/mf_textarea';

type ColumnType = { type: 'input' | 'picklist' | 'textarea' }
export type TextColumn = InputProps<'text'> & ColumnType
export type NumberColumn = InputProps<'number'> & ColumnType
export type PicklistColumn = PicklistProps & ColumnType
export type TextareaColumn = TextareaProps & ColumnType

    export const textColumns:TextColumn[] = [
        {
            label: '場所',
            id: 'place',
            type: 'input',
            input: {
                type: 'text',
                name: 'place',
            }
        }
    ]

    export const numberColumns:NumberColumn[] = [
        {
            label: '風速',
            id: 'wind',
            type: 'input',
            input: {
                type: 'number',
                name: 'wind',
            }
        }
    ]

export const picklistColumns: PicklistColumn[] = [
    {
        label: '釣れた魚',
        id: 'caughtFish',
        type: 'picklist',
        select:{
            name: 'caughtFish',
            multiple: true,
            required: true,
        },
        options: [{
                value: 'aji',
                text: 'アジ',
            },
            {
                value: 'buri',
                text: 'ブリ',
            },
            {
                value: 'kasago',
                text: 'カサゴ',
            },
            {
                value: 'tachiuo',
                text: 'タチウオ',
            },
            {
                value: 'haze',
                text: 'ハゼ',
            }
        ],
    }
]

export const textareaColumns: TextareaColumn[] = [
    {
        label: 'コメント',
        id: 'comment',
        type: 'textarea',
        textarea: {
            name: 'comment',
        },
    }
]
