import { MutableRefObject } from 'react';
import { VFC, useState, useRef, useEffect, ReactElement } from 'react';
import ReactDOM from 'react-dom';
import styled from "styled-components"; 
import GlassImage from "./images/GlassImage.png";
import PenImage from "./images/PenImage.png";
import HomeImage from "./images/HomeImage.png";
import GearImage from "./images/GearImage.png";

const Header: VFC = () => {
    const [isOpen, setIsOpen] = useState(false);
    const dropdownRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        document.addEventListener("mousedown", handleOutsideClick);
        return () => document.removeEventListener("mousedown", handleOutsideClick);
    }, []);

    const handleOutsideClick = (e: Event) => {
        console.log(dropdownRef.current);
        if (!(e.target instanceof HTMLDivElement)) {
            return;
        }
        if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
            setIsOpen(false);
        }
    };

    return (
        <div ref={dropdownRef} style={{background:'black'}}>
            <a href=""><img src={HomeImage} alt="home"></img></a>
            <a href=""><img src={PenImage} alt="edit"></img></a>
            <a href=""><img src={GlassImage} alt="search"></img></a>
            <button  onClick={()=>{setIsOpen(!isOpen)}} type="button">
                <img src={GearImage}></img>
            </button>
            {isOpen && (
                <div>
                    <ul>
                    <li><a>menu1</a></li>
                    <li><a>menu2</a></li>
                    <li><a>menu3</a></li>
                    </ul>
                </div>
            )}
        </div>
    )
}

export default Header
