import { VFC, useState, useRef, useEffect} from 'react';
import GlassImage from "./images/GlassImage.png";
import PenImage from "./images/PenImage.png";
import HomeImage from "./images/HomeImage.png";
import GearImage from "./images/GearImage.png";

const Header: VFC = () => {
    const [isOpen, setIsOpen] = useState(false);
    const divRef = useRef(null);
    const buttonRef = useRef(null);

    // useEffect(() => {
    //     document.addEventListener("mousedown", handleOutsideClick);
    //     return () => document.removeEventListener("mousedown", handleOutsideClick);
    // }, []);

    const handleOutsideClick = () => {
        console.log(divRef.current);
        console.log(buttonRef.current);
    //     if (!(e.target instanceof HTMLImageElement)) {
    //     console.log("return");
    //     console.log("↓e.target↓");
    //     console.log(e.target);
    //         return;
    //     }
    //     if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
    //         console.log("blur!");
    //         setIsOpen(false);
    //     }
    };
    
    return (
        <div style={{background:"black"}}>
            <a href=""><img src={HomeImage} alt="home"></img></a>
            <a href=""><img src={PenImage} alt="edit"></img></a>
            <a href=""><img src={GlassImage} alt="search"></img></a>
            <div style={{color: "red"}} ref={divRef}>DIV1</div>
            <div style={{color: "yellow"}} ref={divRef}>DIV2</div>
            <span>
                <button  onClick={()=>{handleOutsideClick()}} type="button" onBlur={()=>{setIsOpen(false)}} style={{background:"black"}} ref={buttonRef}>
                    <img src={GearImage} ></img>
                </button>
                {isOpen && (
                    <span style={{background:"white"}}>
                        <ul>
                        <li><a href="">menu1</a></li>
                        <li><a>menu2</a></li>
                        <li><a>menu3</a></li>
                        </ul>
                    </span>
                )}
            </span>
        </div>
    )
}

export default Header
