import { VFC } from 'react';
import styled from "styled-components";
import character from 'const/character';
import { PicklistProps } from 'component/column/columnDef'


export const MFPicklist: VFC<PicklistProps> = ({label,id,select,value, options, handleChange}) =>{
    // console.log('★MFPicklist')
    // console.log(value)
    return(
        <StyledItem>
            <StyledLabel htmlFor={id}>{label}</StyledLabel>
            <StyledSelect
                id={id}
                name={select.name}
                value={value ?? undefined}
                required={select.required ?? false}
                onChange={e => handleChange(e.target.value)}
            >
                <option hidden>選択してください</option>
                {options.map((o)=>(
                    <option value={o.value} key={o.text}>{o.value}</option>
                    ))}
            </StyledSelect>
        </StyledItem>
    )
}

const StyledItem =styled.div`
margin-left: auto;
margin-right: auto;
font-family: ${character.FONT};
font-size:  ${character.SIZE.LARGE}px;
`;

const StyledLabel =styled.label`
display: inline-block;
vertical-align: top;
display: flex;
align-items: center;
height: 40px
border: solid 5px #5250e0;
`;

const StyledSelect =styled.select`
width: 300px;
height: auto;
border-radius: 5px;
border: 0.2px solid #9c9c9c;
`;
