import { VFC, useState, useRef, useEffect} from 'react';
import GlassImage from './images/GlassImage.png';
import PenImage from './images/PenImage.png';
import HomeImage from './images/HomeImage.png';
import GearImage from './images/GearImage.png';

const Header: VFC = () => {
    const [isOpen, setIsOpen] = useState(false);
    const menuRef = useRef<HTMLUListElement>(null!);

    // useEffect(() => {
    //     document.addEventListener('mousedown', handleOutsideClick);
    //     return () => document.removeEventListener('mousedown', handleOutsideClick);
    // }, []);

    const handleOutsideClick = () => {
        if (menuRef.current && menuRef.current.className!=='parent') {
            setIsOpen(false);
        }
    };

    const handleMenuClick = ()=> {
        setIsOpen(!isOpen);
        if(isOpen === true) menuRef.current.focus();
    }
    
    return (
        <div style={{background:'black'}}>
            <a href=''><img src={HomeImage} alt='home'></img></a>
            <a href=''><img src={PenImage} alt='edit'></img></a>
            <a href=''><img src={GlassImage} alt='search'></img></a>
            <span className='parent'>
                <button  onClick={()=>{handleMenuClick()}} type='button' style={{background:'black'}}>
                    <img src={GearImage} ></img>
                </button>
                {isOpen && (
                    <ul onBlur={()=>{handleOutsideClick()}} style={{background:'white'}} ref={menuRef}>
                    <li><a href=''>menu1</a></li>
                    <li><a>menu2</a></li>
                    <li><a>menu3</a></li>
                    </ul>
                )}
            </span>
        </div>
    )
}

export default Header
