import { VFC } from 'react';
import styled from "styled-components";
import character from 'const/character';

export type PicklistProps = {
    label: string,
    id: string,
    select: {
        name: string,
        multiple?: boolean,
        required?: boolean,
        value?: string[],
    },
    options: {
        value: string,
        text: string,
    }[],
}

export const MFPicklist: VFC<PicklistProps> = ({label,id,select,options}) =>{
    return(
        <StyledItem>
            <StyledLabel htmlFor={id}>{label}</StyledLabel>
            <StyledSelect
                id={id}
                name={select.name}
                value={select.value ? select.multiple ? select.value : select.value[0] : undefined}
                multiple={select.multiple ?? false}
                required={select.required ?? false}
            >
                {options.map((o)=>(
                    <option value={o.value} key={o.text}>{o.value}</option>
                    ))}
            </StyledSelect>
        </StyledItem>
    )
}

const StyledItem =styled.div`
margin-left: auto;
margin-right: auto;
font-family: ${character.FONT};
font-size:  ${character.SIZE.LARGE}px;
`;

const StyledLabel =styled.label`
display: inline-block;
vertical-align: top;
display: flex;
align-items: center;
height: 40px
border: solid 5px #5250e0;
`;

const StyledSelect =styled.input`
width: 300px;
height: 20px;
border-radius: 5px;
border: 0.2px solid #9c9c9c;
`;
