import { useEffect, useState, VFC, SyntheticEvent } from 'react';
import { MFInput } from 'component/column/mf_input';
import { MFPicklist } from 'component/column/mf_picklist';
import { MFMultiPicklist } from 'component/column/mf_multipicklist';
import { MFTextarea } from 'component/column/mf_textarea';
import { MFButton } from 'component/button/mf_button';
import { Column, TextColumn, NumberColumn, PicklistColumn, MultiPicklistColumn, TextareaColumn, Record } from 'component/column/columnDef';
import { textColumns, numberColumns, picklistColumns, multiPicklistColumns, textareaColumns } from 'component/column/columns';
import styled from "styled-components";
import { header, container } from 'const/size';

const RecordEntryForm: VFC = () => {
    // 各項目のuseStateを宣言
    const [place, setPlace] = useState('');
    const [wind, setWind] = useState(0);
    const [caughtFish, setCaughtFIsh] = useState(['']);
    const [weather, setWeather] = useState('');
    const [comment, setComment] = useState('');

    const record: Record[] = [
        {
            id: 'place',
            value: '',
            handleChange: setPlace,
        },
        {
            id: 'wind',
            value: 0,
            handleChange: setWind,
        },
        {
            id: 'caughtFish',
            value: '',
            handleChange: setCaughtFIsh,
        },
        {
            id: 'weather',
            value: '',
            handleChange: setWeather,
        },
        {
            id: 'comment',
            value: '',
            handleChange: setComment,
        },
    ]

    const [orderedColumns, setOrderedColumns] = useState<Column[]>();
    // const orderedColumns = useRef<Column[]>(); これだと初期化処理内でcurrentに値を入れてもうまく項目が表示されない

    // ページ初期化
    useEffect(()=> {       
        (
            async () => {
                console.log('useEffect start');
                // 項目定義情報の取得
                const columns: Column[] = [...textColumns, ...numberColumns, ...picklistColumns, ...multiPicklistColumns, ...textareaColumns];
            
                // サーバーから初期値を取得 await
                const testData = {
                    place: '大洗漁港',
                    wind: 5,
                    caughtFish: ['aji','kasago'],
                    weather: 'rainy',
                    comment: 'testtesttest'
                }

                // サーバーからの値をrecordに注入
                for(let r of record){
                    if(r.id === 'place') r.value = testData.place;
                    if(r.id === 'wind') r.value = testData.wind;
                    if(r.id === 'caughtFish') r.value = testData.caughtFish;
                    if(r.id === 'weather') r.value = testData.weather;
                    if(r.id === 'comment') r.value = testData.comment;
                }

                // 初期値を各stateにセット
                setPlace(testData.place);
                setWind(testData.wind);
                setCaughtFIsh(testData.caughtFish);
                setWeather(testData.weather);
                setComment(testData.comment);
                
                // columnOrderの順に項目の並び替え
                console.log('★record');
                console.log(record);
                setOrderedColumns(sortColumns(record, columns));
                console.log(orderedColumns);
            })()
        }
    ,[])
    
    const submit =async (e: SyntheticEvent)=> {
        console.log('★submit★');
        e.preventDefault();
        const data = {
            place: place,
            wind: wind,
            caughtFish: caughtFish,
            weather: weather,
            comment: comment,
        }
        console.log('data');
        console.log(data);
    }

    return (
        <StyledOuter>
            <StyledWrapper>
                <form method="post" onSubmit={ submit } >
                    <StyledContainer>
                            {orderedColumns?.map((oc)=>{
                                if(isInput(oc)){
                                    console.log(oc.value);
                                    return <MFInput
                                                label={oc.label}
                                                id={oc.id}
                                                input={oc.input}
                                                key={oc.id}
                                                value={oc.value}
                                                handleChange={oc.handleChange}></MFInput>
                                }else if(isPickList(oc)){
                                    console.log(oc.value);
                                    return <MFPicklist
                                                label={oc.label}
                                                id={oc.id}
                                                select={oc.select}
                                                options={oc.options}
                                                key={oc.id}
                                                value={oc.value}
                                                handleChange={oc.handleChange}></MFPicklist>
                                }else if(isMultiPickList(oc)){
                                    console.log(oc.value);
                                    return <MFMultiPicklist
                                                label={oc.label}
                                                id={oc.id}
                                                select={oc.select}
                                                options={oc.options}
                                                key={oc.id}
                                                value={oc.value}
                                                handleChange={oc.handleChange}></MFMultiPicklist>
                                }else if(isTextarea(oc)){
                                    return <MFTextarea
                                                label={oc.label}
                                                id={oc.id}
                                                textarea={oc.textarea}
                                                key={oc.id}
                                                value={oc.value}
                                                handleChange={oc.handleChange}></MFTextarea>
                                }
                            })}
                    </StyledContainer>
                </form>
                    <StyledButtonWrapper>
                        <StyledButtonContainer>
                            <MFButton type={'submit'} disabled={false} mode={'positive'}>保存</MFButton>
                        </StyledButtonContainer>
                    </StyledButtonWrapper>
            </StyledWrapper>
        </StyledOuter>
    )
}

const sortColumns = (record:Record[], columns:Column[]):Column[] => {
    const orderedColumns: Column[] = [];
    
    for( let r of record ){
        for(let c of columns){
            if(c.id === r.id){
                c.handleChange = r.handleChange;
                c.value = r.value;
                orderedColumns.push(c);
                break;
            }
        }
    }

    return orderedColumns;
}

const isInput = (column: Column): column is (TextColumn | NumberColumn) => column.type === 'input';
const isPickList = (column: Column): column is PicklistColumn => column.type === 'picklist';
const isMultiPickList = (column: Column): column is MultiPicklistColumn => column.type === 'multipicklist';
const isTextarea = (column: Column): column is TextareaColumn => column.type === 'textarea';

const StyledOuter =styled.div`
background-color: #eff0f0;
width: 100vw;
height: 100vw;
`;

const StyledWrapper =styled.div`
margin-top: ${header.height};
width: ${container.WIDTH};
height: 100vw;
margin-left: auto;
margin-right: auto;
background-color: #ffffff;
`;

const StyledContainer =styled.div`
display: grid;
padding-top: 100px;
grid-auto-rows: 150px;
grid-template-columns: 50% 50%;
`;

const StyledButtonWrapper =styled.div`
width: 100%;
height: 100px;
positon: relative;
background-color: red;
`;

const StyledButtonContainer =styled.div`
position: absolute;
top：100px;
right: 100px;
background-color: blue;
`;

export default RecordEntryForm;
