import { VFC } from 'react';
import styled from "styled-components";

type PickListProps = {
    label: string,
    select: {
        id: string,
        name: string,
    },
    options: {
        value: string,
        text: string,
    }[],
    multiple?: boolean,
    required?: boolean,
    defVal?: string,
}

const PickList: VFC<PickListProps> = ({label,select,options,defVal,required,multiple}) =>{
    return(
        <>
            <label htmlFor={select.id}>{label}</label>
            <select id={select.id} name={select.name} value={defVal} multiple={multiple ?? false} required={required ?? false}>
                {options.map((o)=>(
                    <option value={o.value} >{o.value}</option>
                    ))}
            </select>
        </>
    )
}

export default PickList;
