import { VFC } from 'react';
import styled from "styled-components";
import character from 'const/character';
import { InputProps, InputType } from 'component/column/input/columnDef'

export const MFInput: VFC<InputProps<InputType>> = ({label, id, input, value, handleChange}) =>{
    return(
        <StyledItem>
            <StyledLabel htmlFor={id}>{label}</StyledLabel>
            <StyledInput
                id={id}
                name={input.name}
                type={input.type}
                defaultValue={value ?? ''}
                required={input.required ?? false}
                placeholder={input.placeholder ?? ''}
                disabled={input.disabled ?? false}
                maxLength={input.maxlength ?? undefined}
                onChange={e => handleChange(e.target.value)}
            >
            </StyledInput>
        </StyledItem>
    )
}

const StyledItem =styled.div`
margin-left: auto;
margin-right: auto;
font-family: ${character.FONT};
font-size:  ${character.SIZE.LARGE}px;
`;

const StyledLabel =styled.label`
display: inline-block;
vertical-align: top;
display: flex;
align-items: center;
height: 40px
border: solid 5px #5250e0;
`;

const StyledInput =styled.input`
width: 300px;
height: 20px;
border-radius: 5px;
border: 0.2px solid #9c9c9c;
`;
