import { VFC, useState, useRef, useEffect} from 'react';
import styled from "styled-components";
import GlassImage from 'header/images/GlassImage.png';
import PenImage from 'header/images/PenImage.png';
import HomeImage from 'header/images/HomeImage.png';
import GearImage from 'header/images/GearImage.png';
import { IconLink } from 'header/iconLink';
import { container } from 'const/size';

const Header: VFC = () => {
    const [isHidden, setIsHidden] = useState(true);
    const menuRef = useRef<HTMLUListElement>(null);
    const handleClickDocument = useRef((e:Event)=>{});

    useEffect(() => {
        handleClickDocument.current = (e:Event) => {
            if(!(e.target instanceof HTMLElement)) return;
            if(!menuRef.current?.contains(e.target)){
                setIsHidden(true);
                document.removeEventListener('click', handleClickDocument.current);
            }
        }
    },[])

    useEffect(() => {
        isHidden === false && document.addEventListener('click', handleClickDocument.current);
    },[isHidden]);

    const handleMenuClick = ()=> {
        setIsHidden(!isHidden);
    }

    const headerIconLinks =[{
        href: '',
        src: HomeImage,
        alt: 'home',
        width: '40px',
    },{
        href: '',
        src: PenImage,
        alt: 'edit',
        width: '40px',
    },{
        href: '',
        src: GlassImage,
        alt: 'search',
        width: '40px',
    }]

    const configLinks = {

    }
    
    return (
        <StyledWrapper>
            <StyledContainer>
                <StyledFlex>
                    {headerIconLinks.map((headerIconLink)=>(
                        <IconLink
                            href={headerIconLink.href}
                            src={headerIconLink.src}
                            alt={headerIconLink.alt}
                            width={headerIconLink.width}
                        ></IconLink>
                    ))}
                        <StyledConfig onClick={()=>{handleMenuClick()}}>
                            <StyledConfigImg src={GearImage} alt='Setting Menu'></StyledConfigImg>
                            <StyledConfigLinks ref={menuRef} hidden={isHidden}>
                                <li><a>menu1</a></li>
                                <li><a>menu2</a></li>
                                <li><a>menu3</a></li>
                            </StyledConfigLinks>
                        </StyledConfig>
                </StyledFlex>
            </StyledContainer>
        </StyledWrapper>
    )
}

const StyledWrapper =styled.div`
    position: fixed;
    height: 60px;
    background: black;
    top:0;
    left: 0;
    right: 0;
`

const StyledContainer =styled.div`
    width: ${container.WIDTH};
    margin-left: auto;
    margin-right: auto;
`

const StyledFlex =styled.div`
    display: flex;
    positon: relative;
`
const StyledConfig =styled.div`
    margin-left: auto;
    position: relative;
    height: 40px;
    width: 40px;
    `
    
    const StyledConfigImg =styled.img`
    width:40px;
    height:auto;
    padding: 8px 16px;
&:hover{
    background: #383d43;
    transition: 0.2s ;
`

const StyledConfigLinks =styled.ul`
    background: yellow;
    position: absolute;
    top: 100%;
    right: 0;
`

export default Header
