import { VFC, useState, useRef, useEffect} from 'react';
import styled from "styled-components";
import GlassImage from 'header/images/GlassImage.png';
import PenImage from 'header/images/PenImage.png';
import HomeImage from 'header/images/HomeImage.png';
import GearImage from 'header/images/GearImage.png';
import { HeaderIconLink } from 'header/headerIconLink';
import { container } from 'const/size';

const Header: VFC = () => {
    const [isHidden, setIsHidden] = useState(true);
    const menuRef = useRef<HTMLUListElement>(null);
    const handleClickDocument = useRef((e:Event)=>{});

    useEffect(() => {
        handleClickDocument.current = (e:Event) => {
            if(!(e.target instanceof HTMLElement)) return;
            if(!menuRef.current?.contains(e.target)){
                setIsHidden(true);
                document.removeEventListener('click', handleClickDocument.current);
            }
        }
    },[])

    useEffect(() => {
        isHidden === false && document.addEventListener('click', handleClickDocument.current);
    },[isHidden]);

    const handleMenuClick = ()=> {
        setIsHidden(!isHidden);
    }

    const headerIconLinks =[{
        href: '',
        src: HomeImage,
        alt: 'home'
    },{
        href: '',
        src: PenImage,
        alt: 'edit'
    },{
        href: '',
        src: GlassImage,
        alt: 'search'
    }]
    
    return (
        <Wrapper>
            <Container>
                <Flex>
                    {headerIconLinks.map((headerIconLink)=>(
                        <HeaderIconLink
                            href={headerIconLink.href}
                            src={headerIconLink.src}
                            alt={headerIconLink.alt}
                        ></HeaderIconLink>
                    ))}
                        <Config onClick={()=>{handleMenuClick()}}>
                            <ConfigImg src={GearImage} alt='Setting Menu'></ConfigImg>
                            <ConfigLinkList ref={menuRef} hidden={isHidden}>
                                <li><a href=''>menu1</a></li>
                                <li><a>menu2</a></li>
                                <li><a>menu3</a></li>
                            </ConfigLinkList>
                        </Config>
                </Flex>
            </Container>
        </Wrapper>
    )
}

const Wrapper =styled.div`
    position: fixed;
    height: 60px;
    background: black;
    width: 100%;
    top:0;
`

const Container =styled.div`
    width: ${container.WIDTH};
    margin-left: auto;
    margin-right: auto;
`

const Flex =styled.div`
    display: flex;
    positon: relative;
`
const Config =styled.div`
    margin-left: auto;
    `
    
    const ConfigImg =styled.img`
    width:40px;
    height:auto;
    padding: 8px 16px;
&:hover{
    background: #383d43;
    transition: 0.2s ;
`

const ConfigLinkList =styled.ul`
    background: yellow;
`

export default Header
