import { VFC } from 'react';
import styled from "styled-components";

type InputT = 'text' | 'number'

export type InputProps<T extends InputT> = {
    label: string, 
    id: string,
    input: {
        type:T,
        name: string,
        required?: boolean,
        value?: T extends 'text'
            ? string
            : T extends 'number'
            ? number
            : never,
        placeholder?: string,
        disabled?: boolean,
        maxlength?: number,
    },
}

export const MFInput: VFC<InputProps<InputT>> = ({label, id, input}) =>{
    return(
        <StyledItem>
            <label htmlFor={id}>{label}</label>
            <input
                id={id}
                name={input.name}
                type={input.type}
                value={input.value}
                required={input.required ?? false}
                placeholder={input.placeholder ?? ''}
                disabled={input.disabled ?? false}
                maxLength={input.maxlength ?? undefined}
            >
            </input>
        </StyledItem>)
}

const StyledItem =styled.div`
margin-left: auto;
margin-right: auto;
background-color: gray;
`;
