import { VFC } from 'react';
import styled from "styled-components";
import character from 'const/character';

export type TextareaProps = {
    label: string,
    id: string,
    textarea: {
        name: string,
        required?: boolean,
        value?: string,
        placeolder?: string,
        rows?: number,
        cols?: number,
        disabled?: boolean,
        maxlength?: number,
    },
}

export const MFTextarea: VFC<TextareaProps> = ({label,id,textarea}) =>{
    return(
        <StyledItem>
            <StyledLabel htmlFor={id}>{label}</StyledLabel>
                <StyledTextarea
                    id={id}
                    name={textarea.name}
                    value={textarea.value}
                    required={textarea.required ?? false}
                    placeholder={textarea.placeolder ?? ''}
                    cols={textarea.cols ?? 20}
                    rows={textarea.rows ?? undefined}
                    maxLength={textarea.maxlength ?? undefined}
                >
                </StyledTextarea>
        </StyledItem>
    )
}

const StyledItem =styled.div`
margin-left: auto;
margin-right: auto;
font-family: ${character.FONT};
font-size:  ${character.SIZE.LARGE}px;
`;

const StyledLabel =styled.label`
display: inline-block;
vertical-align: top;
display: flex;
align-items: center;
height: 40px
border: solid 5px #5250e0;
`;

const StyledTextarea =styled.textarea`
width: 300px;
height: 100px;
border-radius: 5px;
border: 0.2px solid #9c9c9c;
`;
