import { VFC } from 'react';
import styled from "styled-components";
import character from 'const/character';

type InputT = 'text' | 'number'

export type InputProps<T extends InputT> = {
    label: string, 
    id: string,
    input: {
        type:T,
        name: string,
        required?: boolean,
        value?: T extends 'text'
            ? string
            : T extends 'number'
            ? number
            : never,
        placeholder?: string,
        disabled?: boolean,
        maxlength?: number,
    },
}

export const MFInput: VFC<InputProps<InputT>> = ({label, id, input}) =>{
    return(
        <StyledItem>
            <StyledLabel htmlFor={id}>{label}</StyledLabel>
            <StyledInput
                id={id}
                name={input.name}
                type={input.type}
                value={input.value}
                required={input.required ?? false}
                placeholder={input.placeholder ?? ''}
                disabled={input.disabled ?? false}
                maxLength={input.maxlength ?? undefined}
            >
            </StyledInput>
        </StyledItem>
    )
}

const StyledItem =styled.div`
margin-left: auto;
margin-right: auto;
font-family: ${character.FONT};
font-size:  ${character.SIZE.LARGE}px;
`;

const StyledLabel =styled.label`
position: relative;
left: -15px;
display: inline-block;
vertical-align: top;
display: flex;
align-items: center;
height: 40px
border: solid 5px #5250e0;
`;

const StyledInput =styled.input`
width: 300px;
height: 20px;
border-radius: 5px;
border: 0.2px solid #9c9c9c;
`;
