    //         console.log("blur!");
    //         setIsOpen(false);
    //     }
    // };

    return (
        <div style={{background:"black"}}>
            <a href=""><img src={HomeImage} alt="home"></img></a>
            <a href=""><img src={PenImage} alt="edit"></img></a>
            <a href=""><img src={GlassImage} alt="search"></img></a>
            <button  onClick={()=>{setIsOpen(true)}} type="button" onBlur={()=>{setIsOpen(false)}}>
                <img src={GearImage} ref={dropdownRef}></img>
            </button>
            {isOpen && (
                <div>
                    <ul>
                    <li><a>menu1</a></li>
                    <li><a>menu2</a></li>
                    <li><a>menu3</a></li>
                    </ul>
                </div>
            )}
        </div>
    )
}

export default Header
