import { VFC } from 'react';
import styled from "styled-components";

export type PicklistProps = {
    label: string,
    id: string,
    select: {
        name: string,
        multiple?: boolean,
        required?: boolean,
        value?: string[],
    },
    options: {
        value: string,
        text: string,
    }[],
}

export const MFPicklist: VFC<PicklistProps> = ({label,id,select,options}) =>{
    return(
        <>
            <label htmlFor={id}>{label}</label>
            <select
                id={id}
                name={select.name}
                value={select.value ? select.multiple ? select.value : select.value[0] : undefined}
                multiple={select.multiple ?? false}
                required={select.required ?? false}
            >
                {options.map((o)=>(
                    <option value={o.value} key={o.text}>{o.value}</option>
                    ))}
            </select>
        </>
    )
}
