import { VFC } from 'react';
import { MFPicklist,PicklistProps } from 'component/column/mf_picklist';
import { MFInput, InputProps } from 'component/column/mf_input';
import { MFTextarea, TextareaProps } from 'component/column/mf_textarea';
import { textColumns, numberColumns, pickColumns, areaColumns } from 'component/column/columnDef';
import styled from "styled-components";
import { header } from 'const/size';

type Column = (InputProps<"text"> | InputProps<"number"> | PicklistProps | TextareaProps);

const RecordEntryForm: VFC = () => {
    const order: string[] = [
        'place',
        'wind',
        'caughtFish',
        'comment',
    ];

    const columns: Column[] = [...textColumns, ...numberColumns, ...pickColumns, ...areaColumns];
    const orderedColumns: Column[] = sortColumns(order, columns);
    
    return (
        <StyledWrapper>
            {orderedColumns.map((oc)=>{
                if( isInput(oc) ){
                    return <MFInput label={oc.label} id={oc.id} input={oc.input} type={'input'} key={oc.id}></MFInput>
                }else if( isPickList(oc) ){
                    return <MFPicklist label={oc.label} id={oc.id} select={oc.select} options={oc.options} type={'picklist'} key={oc.id}></MFPicklist>
                }else if( isTextarea(oc)){
                    return <MFTextarea label={oc.label} id={oc.id} textarea={oc.textarea} type={'textarea'} key={oc.id}></MFTextarea>
                }
            })}
        </StyledWrapper>
    )
}

const sortColumns = (order:string[], columns:Column[]):Column[] => {
    const orderedColumns: Column[] = [];

    for( let o of order ){
        for(let c of columns){
            if(o === c.id){
                orderedColumns.push(c);
                break;
            }
        }
    }
    console.log(orderedColumns);
    return orderedColumns;
}

const isInput = (column: Column): column is (InputProps<"text"> | InputProps<"number">) => column.type === 'input';
const isPickList = (column: Column): column is PicklistProps => column.type === 'picklist';
const isTextarea = (column: Column): column is TextareaProps => column.type === 'textarea';

const StyledWrapper =styled.div`
margin-top: ${header.height};
`;

export default RecordEntryForm;
