import { useEffect, useState, VFC, SyntheticEvent } from 'react';
import { MFPicklistMulti } from 'component/column/mf_picklistMulti';
import { MFInput } from 'component/column/mf_input';
import { MFTextarea } from 'component/column/mf_textarea';
import { MFButton } from 'component/button/mf_button';
import { Column, TextColumn, NumberColumn, PicklistColumn, TextareaColumn, Record } from 'component/column/columnDef';
import { textColumns, numberColumns, picklistColumns, textareaColumns } from 'component/column/columns';
import styled from "styled-components";
import { header, container } from 'const/size';

const RecordEntryForm: VFC = () => {
    // 各項目のuseStateを宣言
    const [place, setPlace] = useState('');
    const [wind, setWind] = useState(0);
    const [caughtFish, setCaughtFIsh] = useState(['']);
    const [commnet, setComment] = useState('');

    // テストで初期値としてvalueを設定
    // 実際はサーバから初期値を取得する
    const record: Record[] = [
        {
            id: 'place',
            value: '大洗漁港',
            handleChange: setPlace,
        },
        {
            id: 'wind',
            value: 5,
            handleChange: setWind,
        },
        {
            id: 'caughtFish',
            value: ['アジ', 'ブリ'],
            handleChange: setCaughtFIsh,
        },
        {
            id: 'comment',
            value: 'テストコメント',
            handleChange: setComment,
        },
    ]

    const [orderedColumns, setOrderedColumns] = useState<Column[]>();
    // const orderedColumns = useRef<Column[]>(); これだと初期化処理内でcurrentに値を入れてもうまく項目が表示されない

    // ページ初期化
    useEffect(()=> {
        
        (
            async () => {
                console.log('useEffect start');
                    // 項目定義情報の取得
                    const columns: Column[] = [...textColumns, ...numberColumns, ...picklistColumns, ...textareaColumns];
            
                    // サーバーから初期値を取得
                    
                    // 初期値を各項目にセット
            
                    // columnOrderの順に項目の並び替え
                    setOrderedColumns(sortColumns(record, columns));
                    console.log(orderedColumns);
            })()
        }
    ,[])
    
    const submit =async (e: SyntheticEvent)=> {
        console.log('★submit★');
        e.preventDefault();
        const data = {
            place: place,
            wind: wind,
            caughtFish: caughtFish,
            comment: commnet,
        }
        console.log('data');
        console.log(data);
    }

    return (
        <StyledOuter>
            <StyledWrapper>
                    <form method="post" onSubmit={ submit } >
                        <StyledContainer>
                                {orderedColumns?.map((oc)=>{
                                    if( isInput(oc) ){
                                        return <MFInput
                                                    label={oc.label}
                                                    id={oc.id}
                                                    input={oc.input}
                                                    key={oc.id}
                                                    value={oc.value}
                                                    handleChange={oc.handleChange}></MFInput>
                                    }else if( isPickList(oc) ){
                                        return <MFPicklistMulti
                                                    label={oc.label}
                                                    id={oc.id}
                                                    select={oc.select}
                                                    options={oc.options}
                                                    key={oc.id}
                                                    value={oc.value}
                                                    handleChange={oc.handleChange}></MFPicklistMulti>
                                    }else if( isTextarea(oc)){
                                        return <MFTextarea
                                                    label={oc.label}
                                                    id={oc.id}
                                                    textarea={oc.textarea}
                                                    key={oc.id}
                                                    value={oc.value}
                                                    handleChange={oc.handleChange}></MFTextarea>
                                    }
                                })}
                        </StyledContainer>
                        <MFButton type={'submit'} disabled={false} mode={'negative'}>保存</MFButton>
                    </form>
            </StyledWrapper>
        </StyledOuter>
    )
}

const sortColumns = (record:Record[], columns:Column[]):Column[] => {
    const orderedColumns: Column[] = [];

    for( let r of record ){
        for(let c of columns){
            if(r.id === c.id){
                c.handleChange = r.handleChange;
                c.value = r.value;
                orderedColumns.push(c);
                break;
            }
        }
    }
    return orderedColumns;
}

const isInput = (column: Column): column is (TextColumn | NumberColumn) => column.type === 'input';
const isPickList = (column: Column): column is PicklistColumn => column.type === 'picklist';
const isTextarea = (column: Column): column is TextareaColumn => column.type === 'textarea';

const StyledOuter =styled.div`
background-color: #eff0f0;
width: 100vw;
height: 100vw;
`;

const StyledWrapper =styled.div`
margin-top: ${header.height};
width: ${container.WIDTH};
height: 100vw;
margin-left: auto;
margin-right: auto;
background-color: #ffffff;
`;

const StyledContainer =styled.div`
display: grid;
padding-top: 100px;
grid-auto-rows: 150px;
grid-template-columns: 50% 50%;
`;

export default RecordEntryForm;
