import { VFC } from 'react';
import styled from "styled-components";
import character from 'const/character';
import { MultiPicklistProps, MultiOptions } from 'component/column/columnDef'


export const MFMultiPicklist: VFC<MultiPicklistProps> = ({label,id,select,value, options, handleChange}) =>{
    console.log('★MultiPicklist');
    console.log(value)
    // setDefaultValue(value, options);

    return(
        <StyledItem>
            <StyledLabel htmlFor={id}>{label}</StyledLabel>
            <StyledSelect
                id={id}
                name={select.name}
                multiple
                defaultValue={value}
                required={select.required ?? false}
                onChange={e => handleChange(getValue(e.target.options))}
            >
                {options.map((o)=>(
                    <option value={o.value} key={o.text}>{o.value}</option>
                    ))}
            </StyledSelect>
        </StyledItem>
    )
}

// const setDefaultValue = (value: string[] | undefined, options: MultiOptions)=> {
//     if(value === undefined) return;
//     value.forEach(v=>{
//         for(let o of options){
//             if(o.value === v) o.selected = true;
//         }
//     })
// }

const getValue = (options: HTMLOptionsCollection)=> {
    const values:string[] = [];
    for (let i=0; i<options.length; i++ ){
        if(options[i].selected) values.push(options[i].value);
    }
    return values;
}

const StyledItem =styled.div`
margin-left: auto;
margin-right: auto;
font-family: ${character.FONT};
font-size:  ${character.SIZE.LARGE}px;
`;

const StyledLabel =styled.label`
display: inline-block;
vertical-align: top;
display: flex;
align-items: center;
height: 40px
border: solid 5px #5250e0;
`;

const StyledSelect =styled.select`
width: 300px;
height: auto;
border-radius: 5px;
border: 0.2px solid #9c9c9c;
`;
