import { VFC } from 'react';
import { MFPicklist } from 'component/column/mf_picklist';
import { MFInput } from 'component/column/mf_input';
import { MFTextarea } from 'component/column/mf_textarea';
import { textColumns, numberColumns, picklistColumns, textareaColumns, TextColumn, NumberColumn, PicklistColumn, TextareaColumn } from 'component/column/columnDef';
import styled from "styled-components";
import { header } from 'const/size';
import { container } from 'const/size';

type Column = ( TextColumn | NumberColumn | PicklistColumn | TextareaColumn);

const RecordEntryForm: VFC = () => {
    const order: string[] = [
        'place',
        'wind',
        'caughtFish',
        'comment',
    ];

    const columns: Column[] = [...textColumns, ...numberColumns, ...picklistColumns, ...textareaColumns];
    const orderedColumns: Column[] = sortColumns(order, columns);
    
    return (
        <StyledOuter>
            <StyledWrapper>
                <StyledContainer>
                    {orderedColumns.map((oc)=>{
                        if( isInput(oc) ){
                            return <MFInput label={oc.label} id={oc.id} input={oc.input} key={oc.id}></MFInput>
                        }else if( isPickList(oc) ){
                            return <MFPicklist label={oc.label} id={oc.id} select={oc.select} options={oc.options} key={oc.id}></MFPicklist>
                        }else if( isTextarea(oc)){
                            return <MFTextarea label={oc.label} id={oc.id} textarea={oc.textarea} key={oc.id}></MFTextarea>
                        }
                    })}
                </StyledContainer>
            </StyledWrapper>
        </StyledOuter>
    )
}

const sortColumns = (order:string[], columns:Column[]):Column[] => {
    const orderedColumns: Column[] = [];

    for( let o of order ){
        for(let c of columns){
            if(o === c.id){
                orderedColumns.push(c);
                break;
            }
        }
    }
    return orderedColumns;
}

const isInput = (column: Column): column is (TextColumn | NumberColumn) => column.type === 'input';
const isPickList = (column: Column): column is PicklistColumn => column.type === 'picklist';
const isTextarea = (column: Column): column is TextareaColumn => column.type === 'textarea';

const StyledOuter =styled.div`
background-color: #eff0f0;
width: 100vw;
height: 100vw;
`;

const StyledWrapper =styled.div`
margin-top: ${header.height};
width: ${container.WIDTH};
height: 100vw;
margin-left: auto;
margin-right: auto;
background-color: #ffffff;
`;

const StyledContainer =styled.div`
display: grid;
padding-top: 100px;
grid-auto-rows: 150px;
grid-template-columns: 50% 50%;
`;

export default RecordEntryForm;
