import { VFC, useState, useRef, useEffect} from 'react';
import styled from "styled-components";
import GlassImage from './images/GlassImage.png';
import PenImage from './images/PenImage.png';
import HomeImage from './images/HomeImage.png';
import GearImage from './images/GearImage.png';
import { HeaderIconLink } from './headerIconLink';

const Header: VFC = () => {
    const [isHidden, setIsHidden] = useState(true);
    const menuRef = useRef<HTMLUListElement>(null);
    const handleClickDocument = useRef((e:Event)=>{});

    useEffect(() => {
        handleClickDocument.current = (e:Event) => {
            if(!(e.target instanceof HTMLElement)) return;
            if(!menuRef.current?.contains(e.target)){
                setIsHidden(true);
                document.removeEventListener('click', handleClickDocument.current);
            }
        }
    },[])

    useEffect(() => {
        isHidden === false && document.addEventListener('click', handleClickDocument.current);
    },[isHidden]);

    const handleMenuClick = ()=> {
        setIsHidden(!isHidden);
    }

    const headerIconLinks =[{
        href: '',
        src: HomeImage,
        alt: 'home'
    },{
        href: '',
        src: PenImage,
        alt: 'edit'
    },{
        href: '',
        src: GlassImage,
        alt: 'search'
    }]
    
    return (
        <Wrapper>
            <Flex>
                {headerIconLinks.map((headerIconLink)=>(
                    <HeaderIconLink
                        href={headerIconLink.href}
                        src={headerIconLink.src}
                        alt={headerIconLink.alt}
                    ></HeaderIconLink>
                ))}
                    <div onClick={()=>{handleMenuClick()}}>
                        <ConfigImg src={GearImage} alt='Setting Menu'></ConfigImg>
                    </div>
                        <ul style={{background:'white'}} ref={menuRef} tabIndex={0} hidden={isHidden}>
                            <li><a href=''>menu1</a></li>
                            <li><a>menu2</a></li>
                            <li><a>menu3</a></li>
                        </ul>
            </Flex>
        </Wrapper>
    )
}

const Wrapper =styled.div`
    background: black;
    height: 60px;
`

const Flex =styled.div`
    display: flex;
`

const ConfigImg =styled.img`
    width:40px;
    height:auto;
    padding: 8px 16px;
`

export default Header
