import { TextColumn, NumberColumn, PicklistColumn, MultiPicklistColumn, TextareaColumn } from 'component/column/columnDef';


export const textColumns:TextColumn[] = [
    {
        label: '場所',
        id: 'place',
        type: 'input',
        input: {
            type: 'text',
            name: 'place',
        }
    }
]

export const numberColumns:NumberColumn[] = [
    {
        label: '風速',
        id: 'wind',
        type: 'input',
        input: {
            type: 'number',
            name: 'wind',
        }
    }
]

export const picklistColumns: PicklistColumn[] = [
    {
        label: '天気',
        id: 'weather',
        type: 'picklist',
        select:{
            name: 'weather',
            required: true,
        },
        options: [{
            value: 'none',
            text: ''
            },
            {
                value: 'sunny',
                text: '晴れ',
            },
            {
                value: 'cloudy',
                text: '曇り',
            },
            {
                value: 'rainy',
                text: '雨',
            },
        ],
    }
]

export const multiPicklistColumns: MultiPicklistColumn[] = [
{
    label: '釣れた魚',
    id: 'caughtFish',
    type: 'multipicklist',
    select:{
        name: 'caughtFish',
        required: true,
    },
    options: [{
            value: 'aji',
            text: 'アジ',
        },
        {
            value: 'buri',
            text: 'ブリ',
        },
        {
            value: 'kasago',
            text: 'カサゴ',
        },
        {
            value: 'tachiuo',
            text: 'タチウオ',
        },
        {
            value: 'haze',
            text: 'ハゼ',
        }
    ],
}
]

export const textareaColumns: TextareaColumn[] = [
{
    label: 'コメント',
    id: 'comment',
    type: 'textarea',
    textarea: {
        name: 'comment',
    },
}
]
